let btnShowMoreOptions = document.querySelector('.widget-more-options__show-more-title');
let widgetTitle = document.querySelectorAll('.widget__title');


document.addEventListener("DOMContentLoaded", () => {
	btnShowMoreOptions.addEventListener('click', function(){
		let showMoreItemsBody = btnShowMoreOptions.previousElementSibling;

		if(!showMoreItemsBody.classList.contains('_active')){
			btnShowMoreOptions.innerHTML='Скрыть текст';
		}else{
			btnShowMoreOptions.innerHTML='Показать еще';
		}

		showMoreItemsBody.classList.toggle('_active');
	})

	for (let i = 0; i < widgetTitle.length; i++) {
		widgetTitle[i].addEventListener('click', function(){
			let widgetBody = widgetTitle[i].nextElementSibling;
			widgetTitle[i].classList.toggle('_active');
			widgetBody.classList.toggle('_active');
			
		} )
	}

	let btnShowMore = document.querySelector('.cards__btn-show-more');
	let cardsHidden = document.querySelectorAll('.cards__column-hidden');

	btnShowMore.addEventListener('click', function(){
		btnShowMore.classList.toggle('_active');
		if(btnShowMore.textContent = "Показать еще"){
			btnShowMore.textContent="Скрыть";
		}

		if(!btnShowMore.classList.contains('_active')){
			btnShowMore.textContent = "Показать еще";
		}
		
		for(i=0; i<cardsHidden.length; i++){
			cardsHidden[i].classList.toggle('cards__column-hidden')
		}
	})

 });
