 let formBtnClosenessAny = document.querySelector('.widget-closeness__input--any');
 let formBtnCloseness = document.querySelectorAll('.widget-closeness__input');
 
formBtnClosenessAny.addEventListener('change', function(){
	if(formBtnClosenessAny.checked){
		formBtnCloseness.forEach(function(item){
			item.checked = false;
		})
		formBtnClosenessAny.checked = true;
	}
	
})

formBtnCloseness.forEach(function(item){
	item.addEventListener('change', function(){
		formBtnClosenessAny.checked = false;
	})
})
 
 

